import React from "react";
import { Modal, Button } from "antd";

function FoodOrderDetails({ orders, isVisible, onClose }) {
  return (
    <Modal
      title="Your Orders"
      visible={isVisible}
      onCancel={onClose}
      footer={null}
    >
      {orders.length === 0 ? (
        <p>No items in your cart yet.</p>
      ) : (
        orders.map((order, index) => (
          <div key={index}>
            <p><strong>{order.name}</strong> - ${order.price}</p>
            <p>{order.description}</p>
            <hr />
          </div>
        ))
      )}
      <Button type="primary" onClick={onClose}>
        Close
      </Button>
    </Modal>
  );
}

export default FoodOrderDetails;
