import React, { useState, useEffect } from "react";
import { Card, Row, Col, Button, Modal, notification, Form, Input, Upload } from "antd";
import { UploadOutlined } from "@ant-design/icons";

function FoodMenuScreen() {
  const [foodItems, setFoodItems] = useState([]);
  const [selectedFood, setSelectedFood] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isAddModalVisible, setIsAddModalVisible] = useState(false);
  const [isEditModalVisible, setIsEditModalVisible] = useState(false);
  const [isDeleteModalVisible, setIsDeleteModalVisible] = useState(false);

  useEffect(() => {
    fetchFoodItems();
  }, []);

  const fetchFoodItems = () => {
    const fetchedItems = [
      { key: 1, name: "Hyderabad Biryani", price: 100, description: "Delicious and spicy", image: "data:image/jpeg;base64," },
      { key: 2, name: "Burger", price: 5, description: "Juicy beef burger", image: "/path/to/burger-image.jpg" },
      { key: 3, name: "Pasta", price: 7, description: "Creamy Alfredo pasta", image: "/path/to/pasta-image.jpg" },
      { key: 4, name: "Salad", price: 4, description: "Fresh garden salad", image: "/path/to/salad-image.jpg" },
    ];
    setFoodItems(fetchedItems);
  };

  const handleViewDetails = (food) => {
    setSelectedFood(food);
    setIsModalVisible(true);
  };

  const handleAddToCart = () => {
    notification.success({
      message: "Added to Cart",
      description: `${selectedFood.name} has been added to your cart.`,
    });
    setIsModalVisible(false);
  };

  const handleAddFoodItem = (values) => {
    const newFoodItem = {
      key: foodItems.length + 1,
      name: values.name,
      price: values.price,
      description: values.description,
      image: values.image?.url || "", // Storing image URL if uploaded
    };
    setFoodItems([...foodItems, newFoodItem]);
    setIsAddModalVisible(false);
    notification.success({
      message: "Food Item Added",
      description: `${values.name} has been added to the menu.`,
    });
  };

  const handleEditFoodItem = (values) => {
    const updatedItems = foodItems.map((item) =>
      item.key === selectedFood.key
        ? { ...item, name: values.name, price: values.price, description: values.description, image: values.image?.url || item.image }
        : item
    );
    setFoodItems(updatedItems);
    setIsEditModalVisible(false);
    notification.success({
      message: "Food Item Updated",
      description: `${values.name} has been updated.`,
    });
  };

  const handleDeleteFoodItem = () => {
    const updatedItems = foodItems.filter((item) => item.key !== selectedFood.key);
    setFoodItems(updatedItems);
    setIsDeleteModalVisible(false);
    notification.success({
      message: "Food Item Deleted",
      description: `${selectedFood.name} has been deleted from the menu.`,
    });
  };

  return (
    <div className="ml-3 mt-3 mr-3 bs">
      <h1 className="text-center">Food Menu</h1>
      <div className="mb-3 text-right">
        <Button type="primary" onClick={() => setIsAddModalVisible(true)}>
          Add Menu Item
        </Button>
      </div>
      <Row gutter={[16, 16]}>
        {foodItems.map((food) => (
          <Col key={food.key} xs={24} sm={12} md={8} lg={6}>
            <Card
              title={food.name}
              bordered={true}
              extra={`$${food.price}`}
              actions={[
                <Button type="primary" onClick={() => { setSelectedFood(food); setIsEditModalVisible(true); }}>
                  Edit
                </Button>,
                <Button type="danger" onClick={() => { setSelectedFood(food); setIsDeleteModalVisible(true); }}>
                  Delete
                </Button>,
                <Button type="primary" onClick={() => handleViewDetails(food)}>
                  View Details
                </Button>,
              ]}
            >
              {food.image && <img src={food.image} alt={food.name} style={{ width: '100%', height: 'auto' }} />}
              <p>{food.description}</p>
            </Card>
          </Col>
        ))}
      </Row>

      {/* View Food Details Modal */}
      <Modal
        title={selectedFood?.name}
        visible={isModalVisible}
        onCancel={() => setIsModalVisible(false)}
        footer={[
          <Button key="cancel" onClick={() => setIsModalVisible(false)}>
            Cancel
          </Button>,
          <Button key="add" type="primary" onClick={handleAddToCart}>
            Add to Cart
          </Button>,
        ]}
      >
        {selectedFood?.image && <img src={selectedFood.image} alt={selectedFood?.name} style={{ width: '100%', height: 'auto' }} />}
        <p><strong>Price:</strong> ${selectedFood?.price}</p>
        <p>{selectedFood?.description}</p>
      </Modal>

      {/* Add New Food Item Modal */}
      <Modal
        title="Add New Food Item"
        visible={isAddModalVisible}
        onCancel={() => setIsAddModalVisible(false)}
        footer={null}
      >
        <Form layout="vertical" onFinish={handleAddFoodItem}>
          <Form.Item
            label="Name"
            name="name"
            rules={[{ required: true, message: "Please enter the food name" }]}
          >
            <Input placeholder="Enter food name" />
          </Form.Item>
          <Form.Item
            label="Price"
            name="price"
            rules={[{ required: true, message: "Please enter the price" }]}
          >
            <Input type="number" placeholder="Enter price" />
          </Form.Item>
          <Form.Item
            label="Description"
            name="description"
            rules={[{ required: true, message: "Please enter a description" }]}
          >
            <Input placeholder="Enter description" />
          </Form.Item>
          <Form.Item
            label="Image"
            name="image"
            valuePropName="fileList"
            getValueFromEvent={(e) => (e ? e.fileList : [])}
          >
            <Upload
              name="image"
              listType="picture-card"
              showUploadList={false}
              beforeUpload={() => false} // Prevent auto upload, we are handling the image locally
            >
              <Button icon={<UploadOutlined />}>Upload Image</Button>
            </Upload>
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              Add Food Item
            </Button>
          </Form.Item>
        </Form>
      </Modal>

      {/* Edit Food Item Modal */}
      <Modal
        title="Edit Food Item"
        visible={isEditModalVisible}
        onCancel={() => setIsEditModalVisible(false)}
        footer={null}
      >
        <Form layout="vertical" initialValues={selectedFood} onFinish={handleEditFoodItem}>
          <Form.Item
            label="Name"
            name="name"
            rules={[{ required: true, message: "Please enter the food name" }]}
          >
            <Input placeholder="Enter food name" />
          </Form.Item>
          <Form.Item
            label="Price"
            name="price"
            rules={[{ required: true, message: "Please enter the price" }]}
          >
            <Input type="number" placeholder="Enter price" />
          </Form.Item>
          <Form.Item
            label="Description"
            name="description"
            rules={[{ required: true, message: "Please enter a description" }]}
          >
            <Input placeholder="Enter description" />
          </Form.Item>
          <Form.Item
            label="Image"
            name="image"
            valuePropName="fileList"
            getValueFromEvent={(e) => (e ? e.fileList : [])}
          >
            <Upload
              name="image"
              listType="picture-card"
              showUploadList={false}
              beforeUpload={() => false} // Prevent auto upload, we are handling the image locally
            >
              <Button icon={<UploadOutlined />}>Upload Image</Button>
            </Upload>
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              Update Food Item
            </Button>
          </Form.Item>
        </Form>
      </Modal>

      {/* Delete Food Item Modal */}
      <Modal
        title="Delete Food Item"
        visible={isDeleteModalVisible}
        onCancel={() => setIsDeleteModalVisible(false)}
        footer={null}
      >
        <p>Are you sure you want to delete {selectedFood?.name}?</p>
        <Button type="danger" onClick={handleDeleteFoodItem}>
          Yes, Delete
        </Button>
        <Button onClick={() => setIsDeleteModalVisible(false)}>
          Cancel
        </Button>
      </Modal>
    </div>
  );
}

export default FoodMenuScreen;
