import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table, Button, Popconfirm, message } from "antd";

function AdminDeleteRoom() {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Fetch rooms on component mount
  useEffect(() => {
    const fetchRooms = async () => {
      setLoading(true);
      try {
        const { data } = await axios.get("/api/rooms");
        setRooms(data);
      } catch (err) {
        setError(err.response?.data?.message || err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRooms();
  }, []);

  // Delete a room by ID
  const deleteRoom = async (roomId) => {
    setLoading(true);
    try {
      await axios.delete(`/api/rooms/delete/${roomId}`);
      message.success("Room deleted successfully!");
      setRooms(rooms.filter((room) => room._id !== roomId)); // Remove deleted room from state
    } catch (err) {
      message.error(err.response?.data?.message || "Failed to delete room");
    } finally {
      setLoading(false);
    }
  };


  return (
    <div>
      <h2>Room List</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <Table
        columns={columns}
        dataSource={rooms}
        rowKey="_id"
        loading={loading}
      />
    </div>
  );
}

export default AdminDeleteRoom;
